// import styles from "./Input.module.css";

const Input = (props) => {
  <div
    className={`${styles.control} ${
      props.isValid === false ? styles.invalid : ""
    }`}
  >
    <label htmlFor={props.id}>{props.label}</label>
    <input
      type={props.type}
      id={props.id}
      value={props.value}
      onChange={props.onChange}
      onBlur={props.onBlur}
    />
  </div>;
};

export default Input;
